INSERT INTO projects(project_id, title, description, source_type, study_type, eva_center_name, center_name, taxonomy_id) VALUES('PRJEB6042', 'GEUVADIS: Genetic European Variation in Disease', 'GEUVADIS: Genetic European Variation in Disease (http://www.geuvadis.org), is a European Medical Sequencing Consortium aiming at sharing capacity across Europe in high-throughput sequencing technology to explore genetic variation in health and disease. It is funded by the European Commission 7th framework program under the Coordination and Support Action scheme. It started on the 1st October 2010, and ends on 31st December 2013. The project Coordinator is Xavier Estivill from Center for Genomic Regulation, Barcelona.', 'Germline', 'Aggregate', 'Center for Genomic Regulation - CRG (Barcelona); Institute of Human Genetics - Helmholtz Zentrum (Munich)', 'Center for Genomic Regulation - CRG (Barcelona), Institute of Human Genetics - Helmholtz Zentrum (Munich)', 9606);
