INSERT INTO projects(project_id, title, description, source_type, study_type, eva_center_name, center_name, taxonomy_id) VALUES('PRJEB6911', 'Sanger Institute Mouse Genomes Project v3', 'SNP and INDEL calls from version 3 of the Mouse Genome Project at the Wellcome Trust Sanger Institute. Specifically, this project describes the variants of 18 mouse strains (129P2, 129S1, 129S5, A/J, AKR/J, BALB/cJ, C3H/HeJ, C57BL/6NJ, CAST/EiJ, CBA/J, DBA/2J, FVB/NJ, LPJ, NOD/ShiLtJ, NZOHILtJ, PWK/PhJ, SPRET/EiJ, WSB/EiJ) called against the reference mouse genome sequence GRCm38.', 'Germline', 'Control Set', NULL, 'Wellcome Trust Sanger Institute', 10088);
