INSERT INTO projects(project_id, title, description, source_type, study_type, eva_center_name, center_name, taxonomy_id) VALUES('PRJEB7217', 'UK10K Avon Longitudinal Study of Parents and Children (ALSPAC) Variants', 'The UK10K project proposes a series of complementary genetic approaches to find new low-frequency/rare variants contributing to disease phenotypes. These will be based on obtaining the genome-wide sequence of 4000 samples from the Department of Twin Research and Genetic Epidemiology (TwinsUK) and Avon Longitudinal Study of Parents and Children (ALSPAC) cohorts (at 6x sequence coverage), and the exome sequence (protein-coding regions and related conserved sequence) of 6000 samples selected for extreme phenotypes. This project describes the variants identified from the ALSPAC study. For further information with regard to this cohort please contact Brent Richards (brent.richards@kcl.ac.uk) or Nicole Soranzo (ns6@sanger.ac.uk).', 'Germline', 'Control Set', 'Wellcome Trust Sanger Institute', 'SC', 9606);
INSERT INTO projects(project_id, title, description, source_type, study_type, eva_center_name, center_name, taxonomy_id) VALUES('PRJEB7923', 'Vervet Genetic Mapping Project', 'The Vervet Genetic Mapping Project (VGMP) devloped genomic tools and resources for genetic trait maping in vervet monkey (AKA African green monkey). This project generated genome-wide SNP panels dedicated specifically for genetic mapping in Caribbean-origin sabaeus vervets (Chlorocebus aethiops sabaeus). The Assocation Mapping Panel comprises of ~500K SNPs (r2=0.9). The Linkage Mapping Panel comprises of ~150K SNPs (r2=0.5).', 'Germline', 'Control Set', NULL, 'University of California Los Angeles', 60711);
