INSERT INTO projects(project_id, title, description, source_type, study_type, eva_center_name, center_name, taxonomy_id) VALUES('PRJEB9857', 'Prognostic factors of early response to TKI in chronic myeloid leukemia by whole exome sequencing', 'Chronic myeloid leukemia can be well controlled by targeted tyrosine kinase inhibitors (TKI). However there is about 10% of patients with primary resistance or low efficacy of the therapy. In our study we found promissing new potential early prognostic markers and molecular targets for developing new drugs. Â ', 'Germline', 'Case Control', NULL, 'Federal State Budgetary Institution Â«Research Centre For Medical GeneticsÂ», Moscow, Russia', 9606);
