INSERT INTO projects(project_id, title, description, source_type, study_type, eva_center_name, center_name, taxonomy_id) VALUES('PRJEB8661', 'The Exome Aggregation Consortium (ExAC) v0.3', 'The Exome Aggregation Consortium (ExAC) is a coalition of investigators seeking to aggregate and harmonize exome sequencing data from a variety of large-scale sequencing projects, and to make summary data available for the wider scientific community.', 'Germline', 'Control Set', NULL, 'The Eli and Edythe L. Broad Institute of Harvard and MIT', 9606);
INSERT INTO projects(project_id, title, description, source_type, study_type, eva_center_name, center_name, taxonomy_id) VALUES('PRJEB9858', 'Prognostic factors of stable remission after cessation of TKI therapy in chronic myeloid leukemia by whole exome sequencing', 'Chronic myeloid leukemia can be well controlled by targeted tyrosine kinase inhibitors (TKI). In some patients it is possible to terminate expensive and toxic long-life therapy, however about 50% of these patients relapse without treatment. In our study we found promissing new potential early prognostic markers and molecular targets for developing new drugs.', 'Germline', 'Case Control', NULL, 'Federal State Budgetary Institution Â«Research Centre For Medical GeneticsÂ», Moscow, Russia', 9606);
