INSERT INTO projects(project_id, title, description, source_type, study_type, eva_center_name, center_name, taxonomy_id) VALUES('PRJEB6930', '1000 Genomes Project phase3 release V3+', 'This submission contains variant sites and phased genotypes for all autosomes (excluding a 3Mbps region on chromosome 12 between 7M and 10M) derived from the 1000 Genomes Project phase3 sequence data. ~3400 unphased sites were removed from the submission while they are being investigated. The missing data on chromosome 12 are being patched up and will be submitted at a later time together with the sex chromosomes. This data set is based on 20130502 sequence freeze and alignments; which were collected from 2504 unrelated individuals from 26 populations. The analysis was run using only Illumina platform sequence and only considered sequence with read lengths of 70bp or greater.', 'Germline', 'Control Set', NULL, 'EBI', 9606);
INSERT INTO projects(project_id, title, description, source_type, study_type, eva_center_name, center_name, taxonomy_id) VALUES('PRJEB6040', 'UK10K', 'The UK10K project proposes a series of complementary genetic approaches to find new low-frequency/rare variants contributing to disease phenotypes. These will be based on obtaining the genome-wide sequence of 4000 samples from the TwinsUK and ALSPAC cohorts (at 6x sequence coverage), and the exome sequence (protein-coding regions and related conserved sequence) of 6000 samples selected for extreme phenotypes. Our studies will focus primarily on cardiovascular-related quantitative traits, obesity and related metabolic traits, neurodevelopmental disorders and a limited number of extreme clinical phenotypes that will provide proof-of-concept for future familial trait sequencing. We will directly analyse quantitative traits in the cohorts and the selected traits in the extreme samples, and also use imputation down to 0.1% allele frequency to extend the analyses to further sample sets with genome wide genotype data. In each case we will investigate indels and larger structural variants as well as SNPs, and use statistical methods that combine rare variants in a locus or pathway as well as single-variant approaches. The TwinsUK samples will be part of the cohort study and will undergo whole genome sequencing. For further information with regard to this cohort please contact Brent Richards (brent.richards@kcl.ac.uk) or Nicole Soranzo (ns6@sanger.ac.uk).', 'Germline', 'Control Set', 'Wellcome Trust Sanger Institute', 'SC', 9606);
