INSERT INTO projects(project_id, title, description, source_type, study_type, eva_center_name, center_name, taxonomy_id) VALUES('PRJEB19387', 'Archive of Transcriptomic Variants within the Sheep Milk Somatic Cells', 'Archive with all the variants detected within the sheep milk somatic cell transcriptome. Transcriptome sequencing (RNA-seq) was performed on total RNA extracted from milk somatic cells from ewes on days 10, 50, 120 and 150 after lambing. The experiment was performed in 4 Spanish Churra and 4 Assaf sheep, which differ in their milk production traits. The project Coordinator is Juan JosÃ© Arranz from University of LeÃ³n, Spain.', 'Germline', 'Control Set', NULL, 'University of LeÃ³n', 9940);
INSERT INTO projects(project_id, title, description, source_type, study_type, eva_center_name, center_name, taxonomy_id) VALUES('PRJEB15642', 'Molecular characterisation of Djallonke and Sahelian sheep breed genomes', 'We undertook the first whole genome variant analysis of the Djallonke and the Sahelian sheep breeds of Ghana. WGS from five individuals each of Djallonke and Sahelian sheep was sequenced on an Ion Proton. A total of approximately 404 million (97%) and 343 million (97%) sequence reads from the Djallonke and Sahelian genomes respectively were successfully mapped to the sheep reference genome  Oar_3.1/oviAri3. The mapped samples for each breed were then pooled. Variants were called/genotyped using a pipeline modified from GATK best practices.', 'Germline', 'Control Set', NULL, 'Curtin University', 9940);
INSERT INTO projects(project_id, title, description, source_type, study_type, eva_center_name, center_name, taxonomy_id) VALUES('PRJEB11749', 'HipSci imputed genotyping array data for healthy volunteers', 'The HipSci project brings together diverse constituents in genomics, proteomics, cell biology and clinical genetics to create a UK national iPS cell resource and use it to carry out cellular genetic studies. In this sub-study we performed imputed Genotyping analysis using the Infinium HumanExome BeadChip on iPS cells generated from skin biopsies from healthy volunteers.', 'Germline', 'Control Set', NULL, 'Wellcome Trust Sanger Institute', 9606);
