INSERT INTO projects(project_id, title, description, source_type, study_type, eva_center_name, center_name, taxonomy_id) VALUES('PRJEB19794', 'The Danish reference pan genome phase II', 'Sequencing of Danish parent-offspring trios to determine genomic variation within the Danish population. Phase II comprises of 50 trios sequenced to 80X using Illumina with libraries of insert sizes from 180bp-20kb.', 'Germline', 'Control Set', NULL, 'Technical University of Denmark', 9606);
INSERT INTO projects(project_id, title, description, source_type, study_type, eva_center_name, center_name, taxonomy_id) VALUES('PRJEB20820', 'Deep whole-genome sequencing of 90 Han Chinese genomes', 'We performed whole genome sequencing at high depth (~80X) of 90 unrelated individuals of Chinese ancestry, collected from the 1000 Genome Project samples, including 45 North Han Chinese and 45 South Han Chinese samples. 83 of these 90 have been sequenced by the 1000 Genomes Project. We have identified 12,568,804 single nucleotide polymorphisms and 2,074,210 short InDels from these 90 samples.', 'Germline', 'Control Set', NULL, 'BGI-Shenzhen', 9606);
INSERT INTO projects(project_id, title, description, source_type, study_type, eva_center_name, center_name, taxonomy_id) VALUES('PRJEB20817', 'Haplotype-based stratification of Huntington''s disease', 'Genotypes of 21 common SNPs in Huntington''s disease subjects', 'Germline', 'Control Set', NULL, 'Center for Genomic Medicine, Massachusetts General Hospital', 9606);
INSERT INTO projects(project_id, title, description, source_type, study_type, eva_center_name, center_name, taxonomy_id) VALUES('PRJEB22799', 'GRCz11 Alternate Haplotype Clone Variants', 'The process of producing the GRC zebrafish assembly included the high-quality sequencing and finishing of clones representing alternate haplotypes to corresponding regions in the current primary assembly. This project reports the variation between those alternate haplotype clones and the primary assembly.', 'Germline', 'Control Set', NULL, 'Wellcome Trust Sanger Institute', 7955);
INSERT INTO projects(project_id, title, description, source_type, study_type, eva_center_name, center_name, taxonomy_id) VALUES('PRJEB22988', 'Imputed phased whole genome SNP variants for 163 vervet monkeys', 'Callset of filtered imputed and phased biallelic SNP genotypes of 163 vervet monkeys (genus Chlorocebus) from several African and Caribbean countries as described in Svardal et al. Nature Genetics 2017. Sample information can be found in Data S1 of Svardal et al. 2017.', 'Germline', 'Control Set', NULL, 'Gregor Mendel Institute', 60711);
INSERT INTO projects(project_id, title, description, source_type, study_type, eva_center_name, center_name, taxonomy_id) VALUES('PRJEB9799', 'Whole genome detection of sequences of six horses from diverse breeds.', 'Completed in 2009, the reference genome assembly of the domesticated horse (EquCab 2.0) produced the majority of publically available annotations of genetic variations in this species.  Following that effort, a few other projects that focused at variant discovery of a particular breed or two. In this project we aim to identify and annotate single nucleotide polymorphisms (SNPs), insertions and deletions (INDELs), copy number variations (CNVs) and structural variations (SVs) in the genomes six horses of diverse genetic background using next generation sequencing.', 'Germline', 'Control Set', NULL, 'UNIVERSITY OF FLORIDA', 9796);
INSERT INTO projects(project_id, title, description, source_type, study_type, eva_center_name, center_name, taxonomy_id) VALUES('PRJEB11746', 'HipSci whole exome sequencing data for healthy volunteers', 'The HipSci project brings together diverse constituents in genomics, proteomics, cell biology and clinical genetics to create a UK national iPS cell resource and use it to carry out cellular genetic studies.  In this sub-study we performed whole exome sequencing using Agilent whole exome pulldown method on iPS cells generated from skin biopsies from healthy volunteers.', 'Germline', 'Control Set', NULL, 'The Wellcome Trust Sanger Institute', 9606);
INSERT INTO projects(project_id, title, description, source_type, study_type, eva_center_name, center_name, taxonomy_id) VALUES('PRJEB11745', 'HipSci imputed whole exome sequencing data for healthy volunteers', 'The HipSci project brings together diverse constituents in genomics, proteomics, cell biology and clinical genetics to create a UK national iPS cell resource and use it to carry out cellular genetic studies.  In this sub-study we performed imputed whole exome sequencing using Agilent whole exome pulldown method on iPS cells generated from skin biopsies from healthy volunteers.', 'Germline', 'Control Set', NULL, 'The Wellcome Trust Sanger Institute', 9606);
